import React from 'react';
import { useQuery } from '@apollo/client';
import { QUERY_PROJECTS } from '../utils/queries';

export default function ProjectCard() {
    const { data } = useQuery(QUERY_PROJECTS);
    const projects = data?.projects || [];

  const style = {
    width: 500
  }
 console.log(projects)
  return (

    <div className='list-group  mt-2 w-100' data-bs-theme="dark">
      {projects.map((project) => {
        return (
            <a
        href='#'
        className='list-group-item list-group-item-action'
        aria-current='true'
      >
        <div className='d-flex w-100 justify-content-between'>
          <h5 className='mb-1 text-success'>{project.projectName}</h5>
          <small>{project.deadline}</small>
        </div>
        <p className='mb-1 text-start'>{project.description}</p>
        <div className="progress" role="progressbar" aria-label="Example with label" aria-valuenow="50" aria-valuemin="0" aria-valuemax="100">
          <div className="progress-bar" style={project.completion}>{project.completion}%</div>
        </div>
      </a>
        )
      })}
    </div>
  );
}
